```kotlin
package com.g4mless.taskimport

import android.content.Context // Added
import android.os.Bundle
import androidx.activity.ComponentActivity
import androidx.activity.compose.setContent
import androidx.compose.foundation.clickable
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.Add
import androidx.compose.material.icons.filled.ArrowDropDown
import androidx.compose.material.icons.filled.Delete
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.runtime.getValue // Keep existing imports
import androidx.compose.runtime.setValue // Keep existing imports
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.platform.LocalContext // Added
import androidx.compose.ui.unit.dp
import androidx.compose.ui.window.Dialog
import androidx.datastore.core.DataStore // Added
import androidx.datastore.preferences.core.Preferences // Added
import androidx.datastore.preferences.core.edit // Added
import androidx.datastore.preferences.core.stringPreferencesKey // Added
import androidx.datastore.preferences.preferencesDataStore // Added
import com.g4mless.taskimport.ui.theme.ImportTheme
import kotlinx.coroutines.CoroutineScope // Added
import kotlinx.coroutines.flow.Flow // Added
import kotlinx.coroutines.flow.map // Added
import kotlinx.coroutines.launch // Added
import kotlinx.serialization.Serializable // Added
import kotlinx.serialization.encodeToString // Added
import kotlinx.serialization.json.Json // Added
import kotlinx.serialization.decodeFromString // Added for decoding

// --- DataStore Setup ---
private val Context.dataStore: DataStore<Preferences> by preferencesDataStore(name = "tasks")
private val TASKS_KEY = stringPreferencesKey("tasks_list")
private val json = Json { ignoreUnknownKeys = true; prettyPrint = true } // Added JSON instance

// --- Save/Load Functions ---
suspend fun saveTasks(context: Context, tasks: List<MainActivity.Task>) {
 context.dataStore.edit { settings ->
 val tasksJson = json.encodeToString(tasks)
 settings[TASKS_KEY] = tasksJson
 }
}

fun loadTasks(context: Context): Flow<List<MainActivity.Task>> {
 return context.dataStore.data.map { preferences ->
 val tasksJson = preferences[TASKS_KEY] ?: "[]" // Default to empty list JSON
 try {
 json.decodeFromString<List<MainActivity.Task>>(tasksJson)
 } catch (e: Exception) {
 // Log error or handle corrupted data - returning empty list for now
 println("Error decoding tasks: ${e.message}")
 emptyList()
 }
 }
}
// --- End Save/Load Functions ---


class MainActivity : ComponentActivity() {

 @Serializable // Make Task serializable
 data class Task (
 val id: Int,
 val name: String,
 val importance: Int,
 var isCompleted: Boolean = false // Note: `var` works with DataStore + serialization
 )

 override fun onCreate(savedInstanceState: Bundle?) {
 super.onCreate(savedInstanceState)
 setContent {
 ImportTheme { // Assuming you have a theme wrapper
 TodoApp()
 }
 }
 }

 @OptIn(ExperimentalMaterial3Api::class)
 @Composable
 fun TodoApp() {
 val context = LocalContext.current
 val scope = rememberCoroutineScope()

 // Load tasks flow and collect as state
 val tasksFlow = remember { loadTasks(context) }
 // Start with null to represent the loading state
 val loadedTasksState = tasksFlow.collectAsState(initial = null)

 // Local UI state for the list and next ID
 var taskList by remember { mutableStateOf<List<Task>?>(null) }
 var nextId by remember { mutableIntStateOf(0) }
 var showAddTaskDialog by remember { mutableStateOf(false) }

 // Effect to initialize/update local state when DataStore loads
 LaunchedEffect(loadedTasksState.value) {
 val loadedTasks = loadedTasksState.value
 if (loadedTasks != null && taskList == null) { // Initial load success
 taskList = loadedTasks
 // Calculate nextId based on the max existing ID
 nextId = (loadedTasks.maxOfOrNull { it.id } ?: -1) + 1
 }
 // Optional: Handle subsequent updates from DataStore if needed
 }

 // Helper function to update UI state and save to DataStore
 val updateAndSaveTasks: (List<Task>) -> Unit = { updatedList ->
 taskList = updatedList // Update local UI state first
 scope.launch { // Launch background task to save
 saveTasks(context, updatedList)
 }
 }

 // Function to delete a task
 val deleteTask: (Task) -> Unit = { taskToDelete ->
 taskList?.let { currentList -> // Only modify if list is loaded
 val updatedList = currentList.filter { it.id != taskToDelete.id }
 updateAndSaveTasks(updatedList)
 }
 }

 // Function to toggle task completion status
 val toggleTaskCompletion: (Task) -> Unit = { taskToToggle ->
 taskList?.let { currentList -> // Only modify if list is loaded
 val updatedList = currentList.map { task ->
 if (task.id == taskToToggle.id) {
 task.copy(isCompleted = !task.isCompleted)
 } else {
 task
 }
 }
 updateAndSaveTasks(updatedList)
 }
 }

 // Function to add a task
 val addTask: (String, Int) -> Unit = { name, importance ->
 taskList?.let { currentList -> // Only add if list is loaded
 val currentNextId = nextId // Capture ID before update
 val newTask = Task(currentNextId, name, importance)
 // Ensure the new task is added and list remains sorted
 val updatedList = (currentList + newTask).sortedByDescending { it.importance }
 nextId = currentNextId + 1 // Increment ID for the *next* task
 showAddTaskDialog = false // Close dialog
 updateAndSaveTasks(updatedList) // Update state and save
 }
 }

 Scaffold(
 modifier = Modifier.fillMaxSize(),
 topBar = {
 TopAppBar(title = { Text("Task with Importance") })
 },
 floatingActionButton = {
 FloatingActionButton(onClick = { showAddTaskDialog = true }) {
 Icon(Icons.Default.Add, contentDescription = "Add Task")
 }
 }
 ) { padding ->
 Column(
 modifier = Modifier
 .padding(padding)
 .padding(16.dp)
 ) {
 // --- Handle Loading and Empty States ---
 when (taskList) {
 null -> { // Data is loading
 Box(modifier = Modifier.fillMaxSize(), contentAlignment = Alignment.Center) {
 CircularProgressIndicator()
 Text("Loading Tasks...", modifier = Modifier.padding(top = 60.dp)) // Added text
 }
 }
 emptyList<Task>() -> { // Data loaded, but list is empty
 Box(modifier = Modifier.fillMaxSize(), contentAlignment = Alignment.Center) {
 Text("No tasks yet. Add one!")
 }
 }
 else -> { // Data loaded and list is not empty
 LazyColumn {
 items(taskList!!) { task -> // Use !! because null case is handled
 TaskItem(
 task = task,
 onDelete = deleteTask,
 onToggleComplete = toggleTaskCompletion
 )
 }
 }
 }
 }
 // --- End Loading/Empty State Handling ---
 }
 }

 if (showAddTaskDialog) {
 // Pass the potentially updated addTask lambda
 AddTaskDialog(
 onDismiss = { showAddTaskDialog = false },
 onAddTask = addTask
 )
 }
 }

 @Composable
 fun TaskItem(task: Task, onDelete: (Task) -> Unit, onToggleComplete: (Task) -> Unit) {
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 4.dp)
 ) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.SpaceBetween
 ) {
 Checkbox(
