 @OptIn(ExperimentalMaterial3Api::class)
 @Composable
 fun TodoApp() {
 val context = LocalContext.current
 val scope = rememberCoroutineScope()

 // Load tasks flow and collect as state
 val tasksFlow = remember { loadTasks(context) }
 // Start with null to represent the loading state
 val loadedTasksState = tasksFlow.collectAsState(initial = null)

 // Local UI state for the list and next ID
 var taskList by remember { mutableStateOf<List<Task>?>(null) }
 var nextId by remember { mutableIntStateOf(0) }
 var showAddTaskDialog by remember { mutableStateOf(false) }

 // Effect to initialize/update local state when DataStore loads
 LaunchedEffect(loadedTasksState.value) {
 val loadedTasks = loadedTasksState.value
 if (loadedTasks != null && taskList == null) { // Initial load success
 taskList = loadedTasks
 // Calculate nextId based on the max existing ID
 nextId = (loadedTasks.maxOfOrNull { it.id } ?: -1) + 1
 }
 // Optional: Handle subsequent updates from DataStore if needed
 }

 // Helper function to update UI state and save to DataStore
 val updateAndSaveTasks: (List<Task>) -> Unit = { updatedList ->
 taskList = updatedList // Update local UI state first
 scope.launch { // Launch background task to save
 saveTasks(context, updatedList)
 }
 }

 // Function to delete a task
 val deleteTask: (Task) -> Unit = { taskToDelete ->
 taskList?.let { currentList -> // Only modify if list is loaded
 val updatedList = currentList.filter { it.id != taskToDelete.id }
 updateAndSaveTasks(updatedList)
 }
 }

 // Function to toggle task completion status
 val toggleTaskCompletion: (Task) -> Unit = { taskToToggle ->
 taskList?.let { currentList -> // Only modify if list is loaded
 val updatedList = currentList.map { task ->
 if (task.id == taskToToggle.id) {
 task.copy(isCompleted = !task.isCompleted)
 } else {
 task
 }
 }
 updateAndSaveTasks(updatedList)
 }
 }

 // Function to add a task
 val addTask: (String, Int) -> Unit = { name, importance ->
 taskList?.let { currentList -> // Only add if list is loaded
 val currentNextId = nextId // Capture ID before update
 val newTask = Task(currentNextId, name, importance)
 // Ensure the new task is added and list remains sorted
 val updatedList = (currentList + newTask).sortedByDescending { it.importance }
 nextId = currentNextId + 1 // Increment ID for the *next* task
 showAddTaskDialog = false // Close dialog
 updateAndSaveTasks(updatedList) // Update state and save
 }
 }

