 null -> { // Data is loading
 Box(modifier = Modifier.fillMaxSize(), contentAlignment = Alignment.Center) {
 CircularProgressIndicator()
 Text("Loading Tasks...", modifier = Modifier.padding(top = 60.dp)) // Added text
 }
 }
 emptyList<Task>() -> { // Data loaded, but list is empty
 Box(modifier = Modifier.fillMaxSize(), contentAlignment = Alignment.Center) {
 Text("No tasks yet. Add one!")
 }
 }
 else -> { // Data loaded and list is not empty
 LazyColumn {
 // Add key for better performance and stability
 items(taskList!!, key = { task -> task.id }) { task -> // Use !! because null case is handled
 TaskItem(
 task = task,
 onDelete = deleteTask,
 onToggleComplete = toggleTaskCompletion
 )
 }
 }
 }
 }
 // --- End Loading/Empty State Handling ---
 }
