 val scope = rememberCoroutineScope()

 val snackbarHostState = remember { SnackbarHostState() }
 val operationResult by viewModel.operationResult.collectAsStateWithLifecycle()

 LaunchedEffect(operationResult) {
 operationResult?.let { message ->
 scope.launch { // Pastikan showSnackbar dipanggil dalam scope
 snackbarHostState.showSnackbar(message = message)
 }
 viewModel.clearOperationResult() // Bersihkan pesan setelah ditampilkan
