 val createJsonFileLauncher = rememberLauncherForActivityResult(
 contract = ActivityResultContracts.CreateDocument("application/json"), // Langsung set tipe JSON
 onResult = { uri: Uri? ->
 uri?.let { targetUri ->
 // Langsung siapkan data JSON
 val fileContent: String? = viewModel.prepareJsonExportData()

 if (fileContent != null) {
 scope.launch(Dispatchers.IO) {
 try {
 context.contentResolver.openOutputStream(targetUri)?.use { outputStream ->
 outputStream.write(fileContent.toByteArray())
 outputStream.flush()
 } ?: throw Exception("Failed to open output stream")
 withContext(Dispatchers.Main) {
 snackbarHostState.showSnackbar("Export successful!")
 }
 } catch (e: Exception) {
 e.printStackTrace()
 withContext(Dispatchers.Main) {
 snackbarHostState.showSnackbar("Export failed: ${e.message}")
 }
 }
 }
 } else {
 scope.launch{ snackbarHostState.showSnackbar("Failed to prepare export data.") }
 }
 }
 }
 )

 val openFileLauncher = rememberLauncherForActivityResult(
 contract = ActivityResultContracts.OpenDocument(),
 onResult = { uri: Uri? ->
 uri?.let { sourceUri ->
 val type = context.contentResolver.getType(sourceUri)
 if (type == "application/json" || sourceUri.path?.endsWith(".json", ignoreCase = true) == true) {
 importFileType = "json"
 showImportConfirmDialog = sourceUri
 } else if (type == "text/csv" || type == "text/comma-separated-values" || sourceUri.path?.endsWith(".csv", ignoreCase = true) == true) {
 importFileType = "csv"
 showImportConfirmDialog = sourceUri
 } else {
 scope.launch{ snackbarHostState.showSnackbar("Unsupported or unrecognized file type.") }
 }
 }
 }
